   )
    }}
),

final as (
    select 
        cast(date_day as date) as date_day,
        cast({{ date_trunc('week', 'date_day') }} as date) as date_week,
        cast({{ date_trunc('month', 'date_day') }} as date) as date_month,
        cast({{ date_trunc('quarter', 'date_day') }} as date) as date_quarter,
