macro.balboa.ingest_external_table.macro_sql:
{% macro ingest_external_table(external_source_name) %}
    {#
    Inspired by dbt-external-tables macro
    https://github.com/dbt-labs/dbt-external-tables/blob/main/macros/plugins/snowflake/snowpipe/get_copy_sql.sql
    #}

    {% set target_nodes = [] %}
    {#
    Brute for loop through nodes to grab the one we want.
    This can be extended to support multiple sources, nodes, etc
    #}
    {% set source_nodes = graph.sources.values() if graph.sources else [] %}
    {% for node in source_nodes %}
        {% if node.source_name == external_source_name.strip() %}
            {{ log("Node acquired") }}
            {% do target_nodes.append(node) %}
        {% endif %}
    {% endfor %}

    {% if target_nodes | length == 0 %}
        {{ log("Missing target node - please check it matches existing source") }}
        {{ return(none) }}
    {% endif %}

    {{ log("Executing copy from stage") }}
    {%- set columns = target_nodes[0].columns.values() -%}
    {%- set external = target_nodes[0].external -%}
    {%- set is_csv = dbt_external_tables.is_csv(external.file_format) %}
    {%- set explicit_transaction = true -%}
    {%- set copy_into_target = source(target_nodes[0].source_name, target_nodes[0].name) -%}

    {{ log("Creating table if not exists") }}
    {%- call statement('create_table', fetch_result=True) %}
    create table if not exists {{ copy_into_target }}
     (
        value           variant,
        filename        varchar,
        rownumber       integer,
        _dbt_copied_at  timestamp
    );
    {% endcall %}

    {%- if explicit_transaction -%} begin; {%- endif %}

    {{ log("Executing copy from stage") }}
    {%- call statement('copy_execution', fetch_result=True) %}
        copy into {{ copy_into_target }}
        from (
            select
            {% if columns|length == 0 %}
                $1::variant as value,
            {% else -%}
            {%- for column in columns -%}
                {%- set col_expression -%}
                    {%- if is_csv -%}nullif($ {{ loop.index }},'') {# special case: get columns by ordinal position #}
                    {%- else -%}nullif($1:{{ column.name }},'')   {# standard behavior: get columns by name #}
                    {%- endif -%}
                {%- endset -%}
                {{ col_expression }}::{{ column.data_type }} as {{ column.name }},
            {% endfor -%}
            {% endif %}
                metadata$filename::varchar as metadata_filename,
                metadata$file_row_number::bigint as metadata_file_row_number,
                current_timestamp::timestamp as _dbt_copied_at
            from {{ external.location }} {# stage #}
        )
        file_format = {{ external.file_format }}
        {% if external.pattern -%} pattern = '{{ external.pattern }}' {%- endif %}
        ;
        {% if explicit_transaction -%} commit; {%- endif -%}
    {%- endcall -%}

    {{ log("Executed copy from stage. Exiting macro...") }}

{% endmacro %}

macro.balboa.create_row_access_policy_re.macro_sql:
{% macro create_row_access_policy_region(node_database,node_schema) %}
{# 
  This should be run by dbt-snow-mask, but as this does not currently support row masking, 
  is is manually run for now.

    To run: 
    dbt run-operation create_row_access_policy_region --args '{node_database: prd_commercial_dw, node_schema: source_marketedge}'

  To apply to a table / view:
  alter [table/view] <table_name> add row access policy z_policy_row_region_de on [country code column]

  alter view MARKETEDGE_INTERNAL add row access policy z_policy_row_region on (ISO3_COUNTRY_CODE);

  Policy must be dropped from table / view in order to recreate it
  alter [table/view] <table_name> drop row access policy z_policy_row_region_de

  alter view MARKETEDGE_INTERNAL drop row access policy z_policy_row_region;

#}

{# Rows with removal comments should be removed when transitioning to the dbt-snow-mask approach. #}

  {% set create_policy_sql %} {# to remove #}
    use role securityadmin;
    create or replace row access policy {{node_database}}.{{node_schema}}.z_policy_row_region 
    as (country_code string) 
      returns boolean ->
          case 
            when is_role_in_session('Z_POLICY_ROW_REGION_ALL') then true
            when country_code = 'DEU' AND is_role_in_session('Z_POLICY_ROW_REGION_DE') then true
            else false
          end;
    grant apply on row access policy {{node_database}}.{{node_schema}}.z_policy_row_region to role transformer_dbt_prd;
  {% endset %} {# to remove #}
  {% do run_query(create_policy_sql) %} {# to remove #}
  {{ log("Created policy: " ~ create_policy_sql, info=true) }}
{% endmacro %}

macro.balboa.drop_old_relations.macro_sql:
{% macro drop_old_relations(
        dry_run = 'false'
    ) %}
    {% if execute %}
        {% set current_models = [] %}
        {% for node in graph.nodes.values() | selectattr(
                "resource_type",
                "in",
                ["model", "seed", "snapshot"]
            ) %}
            {% if node.alias %}
                {% do current_models.append(
                    (node.alias, node.schema)
                ) %}
            {% elif node.config.materialization != "ephemeral" %}
                {% do current_models.append(
                    (node.name, node.schema)
                ) %}
            {% endif %}
        {% endfor %}
    {% endif %}

    {% set cleanup_query %}
    WITH models_to_drop AS (
        SELECT
            CASE
                WHEN table_type = 'BASE TABLE' THEN 'TABLE'
                WHEN table_type = 'VIEW' THEN 'VIEW'
            END AS relation_type,
            concat_ws(
                '.',
                table_catalog,
                table_schema,
                '"' || table_name || '"'
            ) AS relation_name
        FROM
            {{ target.database }}.information_schema.tables
        WHERE
            table_schema ILIKE '{{ target.schema }}%'
            AND relation_name NOT IN ({%- for model, schema in current_models -%}
                '{{ target.database }}.{{ schema.upper() }}."{{ model.upper() }}"' {%- if not loop.last -%},
                {% endif %}
            {%- endfor -%})
    )
SELECT
    'DROP ' || relation_type || ' ' || relation_name || ';' AS drop_commands
FROM
    models_to_drop -- intentionally exclude unhandled table_types, including 'external table`
WHERE
    drop_commands IS NOT NULL {% endset %}
    {% set drop_commands = run_query(cleanup_query).columns [0].values() %}
    {% if drop_commands %}
        {% for drop_command in drop_commands %}
            {% do log(
                drop_command,
                True
            ) %}
            {% if dry_run == 'false' %}
                {% do run_query(drop_command) %}
            {% endif %}
        {% endfor %}
    {% else %}
        {% do log(
            'No relations to clean.',
            True
        ) %}
    {% endif %}
{%- endmacro -%}

