    {{ print("Uploading manifest for dbt version: " ~ dbt_version) }}

    {% set set_date_query %}
        SELECT TO_CHAR(SYSTIMESTAMP(), 'YYYYMMDD_HH24MISS') as timestamp, TO_CHAR(SYSTIMESTAMP(), 'YYYYMM') as day_month;
    {% endset %}
